mapper.update:
 
		UPDATE
			t_file AS tf
		SET
			tf.represent_img=#{representImg}
		WHERE
			tf.file_cd = #{fileCd}

mapper.insert:
/*파일 업로드*/
INSERT INTO t_file (
	file_cd
	,original_file_nm
	,stored_file_path
	,file_size
	,re_file_name
	,uploader_id
	,upload_date
	,represent_img
) VALUES

	(
		#{item.fileCd}
		,#{item.originalFileName}
		,#{item.storedFilePath}
		,#{item.fileSize}
		,#{item.reFileName}
		,#{item.userId}
		,NOW()
		,#{item.representImg}
	)

