mapper.update:
UPDATE lesson
SET
	lesson_division=#{lessonDivision}
	,facility_goods_cd=#{facilityGoodsCd}
	,sports_cd=#{sportsCd}
	,user_id=#{userId}
	,lesson_nm=#{lessonName}
	,lesson_detail=#{lessonDetail}
	,lesson_total_member=#{lessonTotalMember}
	,lesson_start_date=#{lessonStartDate}
	,lesson_end_date=#{lessonEndDate}
	,lesson_price=#{lessonPrice}
WHERE
	lesson_cd=#{lessonCd}

mapper.insert:
INSERT INTO lesson
(
	lesson_cd
	,lesson_division
	,facility_goods_cd
	,facility_cd
	,goods_ctg_cd
	,sports_cd
	,user_id
	,lesson_nm
	,lesson_detail
	,lesson_total_member
	,lesson_start_date
	,lesson_end_date
	,lesson_start_time
	,lesson_end_time
	,lesson_count
	,lesson_price
	,lesson_reg_date
) VALUES (
	sf_new_lesson_cd()
	,#{lessonDivision}
	,#{facilityGoods.facilityGoodsCd}
	,#{facilityCd}
	,#{goodsCtgCd}
	,#{sportsCd}
	,#{userId}
	,#{lessonName}
	,#{lessonDetail}
	,#{lessonTotalMember}
	,#{lessonStartDate}
	,#{lessonEndDate}
	,#{lessonStartTime}
	,#{lessonEndTime}
	,#{lessonCount}
	,#{lessonPrice}
	,NOW()
)

mapper.select:
select lesson_sub.town_cd ,lesson_sub.town_nm ,lesson_sub.area_cd ,lesson_sub.facility_cd ,lesson_sub.main_ctg_cd ,lesson_sub.city_cd ,lesson_sub.facility_use_cd ,lesson_sub.user_id ,lesson_sub.facility_nm ,lesson_sub.facility_address ,lesson_sub.facility_tell ,lesson_sub.business_number ,lesson_sub.facility_reg_date ,lesson_sub.facility_post_num ,lesson_sub.facility_detail_addr ,lesson_sub.lesson_cd ,lesson_sub.lesson_division ,lesson_sub.sports_nm ,lesson_sub.sports_cd ,lesson_sub.lesson_nm ,lesson_sub.lesson_detail ,lesson_sub.lesson_total_member ,lesson_sub.lesson_member ,lesson_sub.lesson_start_date ,lesson_sub.lesson_end_date ,lesson_sub.lesson_start_time ,lesson_sub.lesson_end_time ,lesson_sub.lesson_price ,lesson_sub.facility_goods_cd ,lesson_sub.lesson_reg_date ,lesson_file.file_cd ,lesson_file.original_file_nm ,lesson_file.stored_file_path ,lesson_file.file_size ,lesson_file.re_file_name ,lesson_file.uploader_id ,lesson_file.upload_date ,lesson_file.updated_datetime ,lesson_file.represent_img FROM (SELECT act.town_cd ,act.town_nm ,act.city_cd ,f.facility_cd ,f.main_ctg_cd ,f.facility_use_cd ,f.facility_nm ,f.facility_address ,f.facility_tell ,f.business_number ,f.facility_reg_date ,f.facility_post_num ,f.facility_detail_addr ,f.area_cd ,l.lesson_cd ,l.lesson_division ,l.user_id ,l.sports_nm ,l.sports_cd ,l.lesson_nm ,l.lesson_detail ,l.lesson_total_member ,l.lesson_member ,l.lesson_start_date ,l.lesson_end_date ,l.lesson_start_time ,l.lesson_end_time ,l.lesson_price ,l.facility_goods_cd ,l.lesson_reg_date FROM lesson AS l INNER JOIN facility AS f ON l.facility_cd = f.facility_cd INNER JOIN area_city_town AS act on f.town_cd = act.town_cd) AS lesson_sub LEFT JOIN (SELECT rfwg.facility_goods_cd ,tf.file_cd ,tf.re_file_name ,tf.original_file_nm ,tf.stored_file_path ,tf.file_size ,tf.uploader_id ,tf.upload_date ,tf.updator_id ,tf.updated_datetime ,tf.represent_img FROM rel_file_with_goods AS rfwg INNER JOIN t_file AS tf on tf.file_cd = rfwg.flie_cd WHERE tf.deleted_yn != 'Y' and tf.represent_img ='Y' OR null ORDER BY tf.represent_img ) AS lesson_file on lesson_file.facility_goods_cd = lesson_sub.facility_goods_cd
and lesson_sub.main_ctg_cd = #{mainCtgCd}
and lesson_sub.city_cd = #{cityCd}
and lesson_sub.town_cd = #{townCd}
and lesson_sub.sports_cd = #{sportsCd}
and lesson_sub.lesson_nm LIKE CONCAT('%', #{searchValue}, '%')
order by lesson_sub.lesson_reg_date desc limit #{startRow}, #{rowPerPage}
