mapper.insert:

SELECT
	case
	when COUNT(o.order_cd) = 0 
	then 'order_1'
	else
		CONCAT('order_cd_',MAX(CAST(SUBSTRING_INDEX(o.order_cd,'_',-1) AS UNSIGNED))+1)
	END
FROM
	goods_order AS o							


INSERT INTO goods_order
(
	order_cd,
	user_id, 
	facility_goods_cd, 
	order_uuid_id,
	goods_ctg_cd, 
	order_price, 
	order_user_point, 
	order_used_point, 
	order_pay_price, 
	order_reg_date, 
	order_pay_state,
	pay_type,
	order_del_yn
)VALUES (
	 #{orderCd}
	,#{userId}
	,#{facilityGoodsCd} 
	,#{orderUUID}
	,#{goodsCtgCd}
	,#{orderPrice}
	,#{userPoint} 
	,#{usedPoint} 
	,#{orderPayPrice} 
	,NOW()
	,#{orderPayState}
	,#{payType}
	,'N'
)

mapper.select:
SELECT o.order_cd ,o.user_id ,o.facility_goods_cd ,o.goods_ctg_cd ,o.order_price ,o.order_uuid_id ,o.order_user_point ,o.order_used_point ,o.order_pay_price ,DATE_FORMAT(o.order_reg_date, '%Y-%m-%d') AS order_reg_date ,o.order_pay_state ,f.facility_nm ,f.facility_cd FROM goods_order AS o INNER join facility_goods AS g ON o.facility_goods_cd = g.facility_goods_cd INNER join facility AS f on g.facility_cd = f.facility_cd
g.goods_ctg_cd = #{category}
and o.user_id = #{userId}
ORDER BY o.order_reg_date DESC

mapper.update:
UPDATE goods_order

	
		user_id= #{userId}
	
	
		,facility_goods_cd = #{facilityGoodsCd} 
	
	
		,goods_ctg_cd = #{goodsCtgCd} 
	
	
		,order_price = #{orderPrice} 
	
	
		,order_user_point = #{userPoint} 
	
	
		,order_used_point = #{usedPoint} 
	
	
		,order_pay_price = #{orderPayPrice} 
	
	
		,order_pay_state = #{orderPayState} 
	
	
		order_del_yn = #{orderDelYN} 
	
	
		,order_del_date = NOW()
	

WHERE order_cd=#{orderCd}
