mapper.insert:

select
	case
	when COUNT(o.order_cd) = 0 
	then 'order_cd_1'
	else
		CONCAT('order_cd_',MAX(CAST(SUBSTRING_INDEX(o.order_cd,'_',-1) AS UNSIGNED))+1)
	end
FROM
	goods_order AS o							


INSERT INTO goods_order
(
	order_cd,
	user_id, 
	facility_goods_cd, 
	goods_ctg_cd, 
	order_price, 
	order_user_point, 
	order_used_point, 
	order_pay_price, 
	order_reg_date, 
	order_pay_state
)VALUES (
	 #{orderCd}
	,#{userId}
	,#{facilityGoodsCd} 
	,#{goodsCtgCd}
	,#{orderPrice}
	,#{userPoint} 
	,#{usedPoint} 
	,#{orderPayPrice} 
	,NOW()
	,#{orderPayState}
)
