mapper.insert:
 	
 		
		 select
			case
			when COUNT(p.pass_cd) = 0 
			then 'pass_cd_1'
			else
				CONCAT('pass_cd_',MAX(CAST(SUBSTRING_INDEX(p.pass_cd,'_',-1) AS UNSIGNED))+1)
			end
		FROM
			facility_pass AS p
 		
	 	INSERT INTO facility_pass
		(
			 pass_cd
			,facility_goods_cd
			,facility_cd
			,goods_ctg_cd
			,user_id
			,pass_nm
			,pass_reg_date
			,pass_unit
			,pass_price
			,pass_state
			,pass_end_days
		)
		VALUES (
			 #{passCd}
			,#{facilityGoodsCd}
			,#{facilityCd}
			,#{goodsCtgCd}
			,#{userId}
			,#{passNm}
			, NOW()
			,#{passUnit}
			,#{passPrice}
			,'Y'
			,#{passEndDate}
		)
