mapper.insert:

SELECT
	case
	when COUNT(p.pay_cd) = 0 
	then 'pay_cd_1'
	else
		CONCAT('pay_cd_',MAX(CAST(SUBSTRING_INDEX(p.pay_cd,'_',-1) AS UNSIGNED))+1)
	END
FROM
	pay AS p							

INSERT INTO pay
	(
	pay_cd
	,order_cd
	,user_id
	,pay_used_point
	,pay_method
	,payment_key
	,order_uuid_id
	,pay_price
	,pay_total_price
	,pay_reg_date
	,pay_state
	,pay_del_yn
	)
VALUES (
	#{payCd}
	,#{orderCd}
	,#{userId}
	,#{payUsedPoint}
	,#{payMethod}
	,#{paymentKey}
	,#{orderUUID}
	,#{payPrice}
	,#{payTotalPrice}
	, NOW()
	,#{payState}
	,'N'
	)
