mapper.select:
/*안전점검 등록 시설 목록 총 row(튜플) 수*/
SELECT
	COUNT(*)
FROM
	safety_check;

mapper.delete:
/*안전점검 등록 정보 삭제*/
DELETE 
FROM 
	safety_check 
WHERE 
	safety_check_cd = #{safetyCheckCd};

/*시설 관리자의 안전점검 등록 정보 삭제*/
DELETE 
FROM 
	safety_check 
WHERE 
	safety_check_cd = #{safetyCheckCd};

mapper.select:
/*안전점검 결과 목록 총 row(튜플) 수*/
SELECT
	COUNT(*)
FROM
	safety_result;
