mapper.update:
/*구장단가수정*/
	UPDATE stadium_price
	
		
			facility_stadium_cd = 				#{facilityStadiumCd},
		
		
			sports_cd = 				#{sportsCd},
		
		
			day_price = 				#{dayPrice},
		
		
			night_price = 		#{nightPrice},
		
		
			hol_price = 	#{holPrice}
		
		
	WHERE 
		facility_stadium_cd	=			#{facilityStadiumCd};

/*구장수정*/ UPDATE facility_stadium
facility_goods_cd = #{facilityGoodsCd},
facility_cd = #{facilityCd},
goods_ctg_cd = #{goodsCtgCd},
sports_cd = #{sportsCd},
user_id = #{userId},
facility_stadium_nm = #{facilityStadiumNm},
facility_stadium_detail = #{facilityStadiumDetail}
WHERE facility_stadium_cd = #{facilityStadiumCd};

mapper.select:
/*구장 목록 총 튜플 수*/
SELECT
	COUNT(*)
FROM
	facility_stadium;
