mapper.select:
/*회원 등급 목록 조회*/
SELECT
	 l.level		AS levelNum
	,l.level_nm 		AS levelName
FROM
	user_level AS l;

/*시설 내 회원 목록 총 튜플 수*/
SELECT
	COUNT(*)
FROM
	facility_user AS f;

/*전체 회원 목록 총 튜플 수*/
SELECT
	COUNT(*)
FROM
	user AS u;

mapper.update:
/* 회원 정보 변경 */
UPDATE user

	
		user_password		= #{userPw}
	
	
		,user_nm			= #{userName}
	
	
		,user_address		= #{userAddr}
	
	
		,user_birth			= #{userBirth}
	
	
		,user_email			= #{userEmail}
	
	
		,user_tell			= #{userTell}
	
	
		,user_gender		= #{userGender}
	
	
		,user_drop			= #{userDrop}
	
	
		,user_post_num		= #{userPostNum}
	
	
		,user_detail_addr	= #{userDetailAddr}
	

WHERE
	user_id = #{userId};
