mapper.insert:

	 select
		case
		when COUNT(b.booking_cd) = 0 
		then 'booking_01'
		else
			CONCAT('booking_',MAX(CAST(SUBSTRING_INDEX(b.booking_cd,'_',-1) AS UNSIGNED))+1)
		end
	FROM
		booking AS b
	
INSERT INTO review
	(booking_cd
	,facility_cd
	,facility_goods_cd
	,user_id
	,goods_ctg_cd
	,booking_date
	,booking_start_time
	,booking_end_time
	,booking_state
	,booking_price
	,review_reg_date
	) VALUES (
		#{bookingCd}
		,#{facilityCd}
		,#{facilityGoodsCd}
		,#{userId}
		,#{goodsCtgCd}
		,#{bookingDate}
		,#{bookingStartTime}
		,#{bookingEndTime}
		,'예약완료'
		,#{bookingPrice}
		,CURDATE())
